from __future__ import annotations

from typing import List

from .types import StepResult, TickMetrics


class StatsCollector:
    def __init__(self) -> None:
        self._history: List[TickMetrics] = []

    def record(self, step_result: StepResult) -> TickMetrics:
        world = step_result.world
        population = len(world.agents)

        if population == 0:
            avg_energy = 0.0
        else:
            total_energy = 0
            for _pos, agent in world.agents:
                total_energy += agent.energy
            avg_energy = total_energy / population

        metrics = TickMetrics(
            step_number=world.step_number,
            population=population,
            births=step_result.births,
            deaths=step_result.deaths,
            avg_energy=float(avg_energy),
        )
        self._history.append(metrics)
        return metrics

    def get_history(self) -> list[TickMetrics]:
        return list(self._history)

    def to_csv(self) -> str:
        lines = ["step_number,population,births,deaths,avg_energy"]
        for m in self._history:
            lines.append(
                f"{m.step_number},{m.population},{m.births},{m.deaths},{m.avg_energy}"
            )
        return "\n".join(lines)
