from __future__ import annotations

from statistics import mean

from worldsim.types import StepResult, TickMetrics


class StatsCollector:
    def __init__(self) -> None:
        self._history: list[TickMetrics] = []

    def record(self, step_result: StepResult) -> TickMetrics:
        world = step_result.world
        population = len(world.agents)

        if population == 0:
            avg_energy = 0.0
        else:
            avg_energy = float(mean(agent.energy for _, agent in world.agents))

        area = world.width * world.height
        density = (population / area) if area > 0 else 0.0

        metrics = TickMetrics(
            step_number=world.step_number,
            population=population,
            births=step_result.births,
            deaths=step_result.deaths,
            avg_energy=avg_energy,
            density=density,
        )
        self._history.append(metrics)
        return metrics

    def get_history(self) -> list[TickMetrics]:
        return list(self._history)

    def to_csv(self) -> str:
        header = "step_number,population,births,deaths,avg_energy,density"
        rows = [header]
        for m in self._history:
            rows.append(
                f"{m.step_number},{m.population},{m.births},{m.deaths},{m.avg_energy},{m.density}"
            )
        return "\n".join(rows)
